
public class Calcule {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5, b = 2;
		double c = 5, d = 2;
		int iResultat = a / b;
		int iMod = a % b;
		double dResultat = c / d;
		double dMod = c % d;
		
		System.out.println("Le résultat de a / b est = " + iResultat + " et le modulo est = " + iMod + ". " + "Le résultat de c / d est = " + dResultat + " et le modulo est = " + dMod + ".");
	}

}
